mapper.select:
SELECT



FROM scm_commission



        AND name LIKE CONCAT('%',#{name},'%')


        AND contract_id = #{contractId}


        AND author_id = #{authorId}


        AND receipt_id = #{receiptId}


        AND status = #{status}







        ORDER BY create_time ASC


        ORDER BY create_time DESC




        ORDER BY update_time ASC


        ORDER BY update_time DESC
